package com.example.ceritaku.custom_viewimport android.app.AlertDialogimport android.content.Contextimport android.os.Bundleimport android.widget.Buttonimport android.widget.ImageViewimport android.widget.TextViewimport com.example.ceritaku.Rclass MyAlertDialog(    context: Context,    private val message: Int,    private val image: Int,    private val action: (() -> Unit)? = null) : AlertDialog(context) {    init {        setCancelable(false)    }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.layout_alert)        val messageText = findViewById<TextView>(R.id.tvError)        messageText.text = context.getString(message)        val imageView = findViewById<ImageView>(R.id.ivEmptyUser)        imageView.setImageResource(image)        val setNegativeButton = findViewById<Button>(R.id.dismissButton)        setNegativeButton.setOnClickListener {            dismiss()            action?.invoke()        }    }}