package com.example.ceritaku.diimport android.content.Contextimport androidx.datastore.core.DataStoreimport androidx.datastore.preferences.core.Preferencesimport androidx.datastore.preferences.preferencesDataStoreimport com.example.ceritaku.api.ApiConfigimport com.example.ceritaku.data.AppRepositoryimport com.example.ceritaku.preference.PreferenceLoginval Context.dataStore: DataStore<Preferences> by preferencesDataStore("story")object Injection {    fun provideRepository(context: Context): AppRepository{        val prefe = PreferenceLogin(context)        val apiService = ApiConfig.getApiService()        return AppRepository.getInstance(prefe, apiService)    }}