package com.example.ceritaku.factoryimport android.content.Contextimport androidx.lifecycle.ViewModelimport androidx.lifecycle.ViewModelProviderimport com.example.ceritaku.data.AppRepositoryimport com.example.ceritaku.di.Injectionimport com.example.ceritaku.ui.home.HomeViewModelimport com.example.ceritaku.ui.login.LoginViewModelimport com.example.ceritaku.ui.maps.MapsViewModelimport com.example.ceritaku.ui.sign_up.SignUpViewModelimport com.example.ceritaku.ui.upload.UploadViewModelclass ViewModelFactory private constructor(private val appRepository: AppRepository): ViewModelProvider.NewInstanceFactory(){    @Suppress("UNCHECKED_CAST")    override fun <T : ViewModel> create(modelClass: Class<T>): T {        if (modelClass.isAssignableFrom(HomeViewModel::class.java)){            return HomeViewModel(appRepository) as T        }        if (modelClass.isAssignableFrom(LoginViewModel::class.java)) {            return LoginViewModel(appRepository) as T        }        if (modelClass.isAssignableFrom(SignUpViewModel::class.java)){            return SignUpViewModel(appRepository) as T        }        if (modelClass.isAssignableFrom(MapsViewModel::class.java)){            return MapsViewModel(appRepository) as T        }        if (modelClass.isAssignableFrom(UploadViewModel::class.java)){            return UploadViewModel(appRepository) as T        }        throw IllegalArgumentException("Unknown ViewModel class: " + modelClass.name)    }    companion object{        @Volatile        private var instance: ViewModelFactory? = null        fun getInstnce(context: Context): ViewModelFactory{            return instance ?: synchronized(this){                instance ?: ViewModelFactory(Injection.provideRepository(context))            }.also { instance = it }        }    }}