package com.example.ceritaku.ui.homeimport androidx.lifecycle.LiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport androidx.paging.PagingDataimport androidx.paging.cachedInimport com.example.ceritaku.data.AppRepositoryimport com.example.ceritaku.model.Storyclass HomeViewModel(appRepository: AppRepository): ViewModel() {    val getAllStory: LiveData<PagingData<Story>> =        appRepository.getListStories().cachedIn(viewModelScope)}