package com.example.ceritaku.ui.uploadimport androidx.lifecycle.ViewModelimport com.example.ceritaku.data.AppRepositoryimport okhttp3.MultipartBodyimport okhttp3.RequestBodyclass UploadViewModel(private val appRepository: AppRepository) : ViewModel() {    fun uploadStory(imageFile: MultipartBody.Part, desc: RequestBody, lat: Double, lon: Double) =        appRepository.uploadStory(imageFile, desc, lat, lon)    fun uploadStoryNotLoc(imageFile: MultipartBody.Part, desc: RequestBody) =        appRepository.uploadStoryNotLoc(imageFile, desc)}